    private void jbnLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbnLoginActionPerformed

        Usuarios user = new Usuarios();
        UsuariosDAO ud = new UsuariosDAO();

        user.setNome(txtLogin.getText());
        user.setSenha(txtSenha.getText());
        List<Usuarios> lista = ud.teste(user);

        if (lista.size() >= 1) {
            for (Usuarios u : lista) {

                UsuariosDTO.setUsuarios(u.getNivel());
            }
            JOptionPane.showMessageDialog(null, "bem vindo " + user.getNome() + " seu nivel de acesso Ã© de " + UsuariosDTO.getUsuarios());
            MenuView mv = new MenuView();
            mv.setVisible(true);
            this.dispose();
        } else {
            JOptionPane.showMessageDialog(null, "Login e(ou)Senhas incorretos", "AVISO", 2);
        }


    }//GEN-LAST:event_jbnLoginActionPerformed

